mapper.select:
SELECT 

FROM litemall_qa_statistics
WHERE deleted = 0
  AND user_id = #{userId,jdbcType=INTEGER}

  AND intent_type = #{intentType,jdbcType=VARCHAR}

ORDER BY create_time DESC

SELECT 

FROM litemall_qa_statistics
WHERE deleted = 0
  AND intent_type = #{intentType,jdbcType=VARCHAR}

  AND avg_confidence_score >= #{minConfidence,jdbcType=DECIMAL}

ORDER BY avg_confidence_score DESC

SELECT intent_type, COUNT(*) as count, AVG(avg_confidence_score) as avg_confidence, AVG(avg_response_time) as avg_response_time, SUM(query_count) as total_queries, SUM(success_count) as total_success FROM litemall_qa_statistics WHERE deleted = 0
AND create_time >= #{startTime,jdbcType=TIMESTAMP}
AND create_time <= #{endTime,jdbcType=TIMESTAMP}
GROUP BY intent_type ORDER BY count DESC
LIMIT #{limit,jdbcType=INTEGER}

SELECT user_id, COUNT(DISTINCT session_id) as session_count, SUM(query_count) as total_queries, SUM(success_count) as total_success, AVG(avg_confidence_score) as avg_confidence, AVG(avg_response_time) as avg_response_time, MAX(create_time) as last_active_time FROM litemall_qa_statistics WHERE deleted = 0
AND create_time >= #{startTime,jdbcType=TIMESTAMP}
AND create_time <= #{endTime,jdbcType=TIMESTAMP}
GROUP BY user_id ORDER BY total_queries DESC
LIMIT #{limit,jdbcType=INTEGER}

SELECT COUNT(DISTINCT session_id) as total_sessions, COUNT(DISTINCT user_id) as total_users, SUM(query_count) as total_queries, SUM(success_count) as total_success, AVG(avg_confidence_score) as avg_confidence, AVG(avg_response_time) as avg_response_time, COUNT(DISTINCT intent_type) as total_intent_types FROM litemall_qa_statistics WHERE deleted = 0
AND create_time >= #{startTime,jdbcType=TIMESTAMP}
AND create_time <= #{endTime,jdbcType=TIMESTAMP}

mapper.update:
UPDATE litemall_qa_statistics
SET 
  query_count = query_count + #{queryCount,jdbcType=INTEGER},
  success_count = success_count + #{successCount,jdbcType=INTEGER},
  avg_confidence_score = 
    CASE 
      WHEN query_count = 0 THEN #{confidenceScore,jdbcType=DECIMAL}
      ELSE (avg_confidence_score * query_count + #{confidenceScore,jdbcType=DECIMAL}) / (query_count + #{queryCount,jdbcType=INTEGER})
    END,
  avg_response_time = 
    CASE 
      WHEN query_count = 0 THEN #{responseTime,jdbcType=INTEGER}
      ELSE (avg_response_time * query_count + #{responseTime,jdbcType=INTEGER}) / (query_count + #{queryCount,jdbcType=INTEGER})
    END,
  update_time = #{updateTime,jdbcType=TIMESTAMP}
WHERE session_id = #{sessionId,jdbcType=VARCHAR}
  AND user_id = #{userId,jdbcType=INTEGER}
  AND intent_type = #{intentType,jdbcType=VARCHAR}
  AND deleted = 0
